import {filter} from 'lodash';
import {useEffect, useState} from 'react';
// material
import {
    Card,
    Table,
    Stack,
    TableRow,
    TableBody,
    TableCell,
    Container,
    Typography,
    TableContainer,
    TablePagination,
} from '@mui/material';
// components
import Page from '../components/Page';
import Scrollbar from '../components/Scrollbar';
import SearchNotFound from '../components/SearchNotFound';
import {
    UserListHead,
    UserListToolbar,
    UserMoreMenu,
} from '../components/_dashboard/user';
//
import {unwrapResult} from '@reduxjs/toolkit';
import {useDispatch} from 'react-redux';
import {getBillById, getBills} from '../store/slice/bill';
import FormEditStatus from '../components/bill/FormEditStatus';
import ListBill from '../components/bill/ListBill';

// ----------------------------------------------------------------------

const TABLE_HEAD = [
    {id: 'userName', label: 'Tên', alignRight: false},
    {id: 'phoneNumber', label: 'SĐT', alignRight: false},
    {id: 'address', label: 'Địa chỉ', alignRight: false},
    {id: 'productName', label: 'Sản phẩm', alignRight: false},
    {id: 'productPrice', label: 'Gía lẻ', alignRight: false},
    {id: 'total', label: 'Tổng', alignRight: false},
    {id: 'oderTime', label: 'Oder Time', alignRight: false},
    {id: 'status', label: 'Status', alignRight: false},
    {id: ''},
];

// ----------------------------------------------------------------------

function descendingComparator(a, b, orderBy) {
    if (b[orderBy] < a[orderBy]) {
        return -1;
    }
    if (b[orderBy] > a[orderBy]) {
        return 1;
    }
    return 0;
}

function getComparator(order, orderBy) {
    return order === 'desc'
        ? (a, b) => descendingComparator(a, b, orderBy)
        : (a, b) => -descendingComparator(a, b, orderBy);
}

function applySortFilter(array, comparator, query) {
    const stabilizedThis = array.map((el, index) => [el, index]);
    stabilizedThis.sort((a, b) => {
        const order = comparator(a[0], b[0]);
        if (order !== 0) {
            return order;
        }
        return a[1] - b[1];
    });
    if (query) {
        return filter(
            array,
            (_user) =>
                _user.phoneNumber.toLowerCase().indexOf(query.toLowerCase()) !== -1
        );
    }
    return stabilizedThis.map((el) => el[0]);
}

export default function Bill() {
    const [data, setData] = useState([]);
    const dispatch = useDispatch();
    const [page, setPage] = useState(0);
    const [order, setOrder] = useState('asc');
    const [selected, setSelected] = useState([]);
    const [orderBy, setOrderBy] = useState('name');
    const [filterName, setFilterName] = useState('');
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [openForm, setOpenForm] = useState(false);
    const [openListBill, setListBill] = useState(false);
    const [dataEdit, setDataEdit] = useState([]);
    const [isEdit, setIsEdit] = useState([]);
    const [name, setName] = useState([]);
    const [dataBill, setDataBill] = useState([]);

    useEffect(() => {
        fetchData();
    }, [isEdit]);
    useEffect(() => {
        let datas = data.filter((item) => item.userName === name);
        console.log('first', datas);
        setDataBill(datas);
    }, [name]);

    const fetchData = async () => {
        try {
            const res = await dispatch(getBills());
            setData(res.payload);
            console.log(res.payload);
            unwrapResult(res);
        } catch (e) {
            console.log(e);
        }
    };


    const handleCloseList = () => {
        setOpenForm(false);
    };

    const handleRequestSort = (event, property) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
    };

    const handleSubmit = (value) => {
        setOpenForm(false);
        setDataEdit({});
        value && setIsEdit(value);
    };

    const handleSelectAllClick = (event) => {
        if (event.target.checked) {
            const newSelecteds = data.map((n) => n.name);
            setSelected(newSelecteds);
            return;
        }
        setSelected([]);
    };

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handleFilterByName = (event) => {
        setFilterName(event.target.value);
    };

    const clickButtonEdit = async (id) => {
        try {
            const res = await dispatch(getBillById(id));
            setDataEdit(res.payload);
            setOpenForm(true);
            unwrapResult(res);
        } catch (e) {
            console.log(e);
        }
    };

    const clickButtonDetail = async (name) => {
        setName(name);
        setListBill(true);
    };


    const handleDelete = () => {
        setSelected([]);
    };

    const emptyRows =
        page > 0 ? Math.max(0, (1 + page) * rowsPerPage - data.length) : 0;

    const filteredBills = applySortFilter(
        data,
        getComparator(order, orderBy),
        filterName
    );

    const configColorForItemRow = (status) => {
        switch (status) {
            case'Đang Giao': {
                return 'yellow';
            }
            case 'Giao Thành Công': {
                return 'green';
            }
            case 'Giao Thất Bại': {
                return 'red';
            }
        }
    };

    const isUserNotFound = filteredBills.length === 0;

    return (
        <Page title="User | Minimal-UI">
            <Container>
                <Stack
                    direction="row"
                    alignItems="center"
                    justifyContent="space-between"
                    mb={5}
                >
                    <Typography variant="h4" gutterBottom>
                        Bill
                    </Typography>
                </Stack>

                {openForm && (
                    <FormEditStatus dataEdit={dataEdit} handleSubmit={handleSubmit}/>
                )}
                {openListBill && <ListBill data={dataBill} name={name} handleCloseList={handleCloseList}/>}
                <Card>
                    <UserListToolbar
                        numSelected={selected.length}
                        selected={selected}
                        handleDelete={handleDelete}
                        filterName={filterName}
                        onFilterName={handleFilterByName}
                    />

                    <Scrollbar>
                        <TableContainer sx={{minWidth: 800}}>
                            <Table>
                                <UserListHead
                                    notCheckBox={true}
                                    order={order}
                                    orderBy={orderBy}
                                    headLabel={TABLE_HEAD}
                                    rowCount={data.length}
                                    numSelected={selected.length}
                                    onRequestSort={handleRequestSort}
                                    onSelectAllClick={handleSelectAllClick}
                                />
                                <TableBody>
                                    {filteredBills
                                        .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                        .map((row, index) => {
                                            const {
                                                id,
                                                total,
                                                oderTime,
                                                status,
                                                productName,
                                                productPrice,
                                                userName,
                                                phoneNumber,
                                                address,
                                            } = row;
                                            console.log(productPrice);
                                            const isItemSelected = selected.indexOf(id) !== -1;
                                            return (
                                                <TableRow
                                                    hover
                                                    key={index}
                                                    tabIndex={-1}
                                                    aria-checked={isItemSelected}
                                                    style={{backgroundColor: configColorForItemRow(status)}}
                                                >
                                                    <TableCell component="th" scope="row" padding="30px">
                                                        <Stack
                                                            direction="row"
                                                            alignItems="center"
                                                            spacing={2}
                                                        >
                                                            <Typography variant="subtitle2" noWrap>
                                                                {userName}
                                                            </Typography>
                                                        </Stack>
                                                    </TableCell>
                                                    <TableCell align="left">{phoneNumber}</TableCell>
                                                    <TableCell
                                                        align="left"
                                                        style={{overflowWrap: 'anywhere'}}
                                                    >
                                                        {address}
                                                    </TableCell>
                                                    <TableCell
                                                        align="left"
                                                        style={{overflowWrap: 'anywhere'}}
                                                    >
                                                        {productName}
                                                    </TableCell>
                                                    <TableCell
                                                        align="left"
                                                        style={{overflowWrap: 'anywhere'}}
                                                    >
                                                        {productPrice}
                                                    </TableCell>
                                                    <TableCell align="left">{total}</TableCell>
                                                    <TableCell align="left">{oderTime}</TableCell>
                                                    <TableCell align="left">{status}</TableCell>

                                                    <TableCell align="right">
                                                        <UserMoreMenu
                                                            userId={id}
                                                            name={userName}
                                                            clickButtonEdit={clickButtonEdit}
                                                            clickButtonDetail={clickButtonDetail}
                                                            isDetail={true}
                                                        />
                                                    </TableCell>
                                                </TableRow>
                                            );
                                        })}
                                    {emptyRows > 0 && (
                                        <TableRow style={{height: 53 * emptyRows}}>
                                            <TableCell colSpan={6}/>
                                        </TableRow>
                                    )}
                                </TableBody>
                                {isUserNotFound && (
                                    <TableBody>
                                        <TableRow>
                                            <TableCell align="center" colSpan={6} sx={{py: 3}}>
                                                <SearchNotFound searchQuery={filterName}/>
                                            </TableCell>
                                        </TableRow>
                                    </TableBody>
                                )}
                            </Table>
                        </TableContainer>
                    </Scrollbar>

                    <TablePagination
                        rowsPerPageOptions={[5, 10, 25]}
                        component="div"
                        count={data.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Card>
            </Container>
        </Page>
    );
}
